ns_choice('voyager::media.files', 0) }} @endif @else @include('voyager::multilingual.input-hidden-bread-browse') <span>{{ $data->{$row->field} }}</span> @endif </td> @endforeach <td class="no-sort no-click bread-actions"> @foreach($actions as $action) @if (!method_exists($action, 'massAction')) @include('voyager::bread.partials.actions', ['action' => $action]) @endif @endforeach </td> </tr> @endforeach </tbody> </table> </div> @if ($isServerSide) <div class="pull-left"> <div role="status" class="show-res" aria-live="polite">{{ trans_choice( 'voyager::generic.showing_entries', $dataTypeContent->total(), [ 'from' => $dataTypeContent->firstItem(), 'to' => $dataTypeContent->lastItem(), 'all' => $dataTypeContent->total() ]) }}</div> </div> <div class="pull-right"> {{ $dataTypeContent->appends([ 's' => $search->value, 'filter' => $search->filter, 'key' => $search->key, 'order_by' => $orderBy, 'sort_order' => $sortOrder, 'showSoftDeleted' => $showSoftDeleted, ])->links() }} </div> @endif </div> </div> </div> </div> </div> {{-- Single delete modal --}} <div class="modal modal-danger fade" tabindex="-1" id="delete_modal" role="dialog"> <div class="modal-dialog"> <div class="modal-content"> <div class="modal-header"> <button type="button" class="close" data-dismiss="modal" aria-label="{{ __('voyager::generic.close') }}"><span aria-hidden="true">&times;</span></button> <h4 class="modal-title"><i class="voyager-trash"></i> {{ __('voyager::generic.delete_question') }} {{ strtolower($dataType->getTranslatedAttribute('display_name_singular')) }}?</h4> </div> <div class="modal-footer"> <form action="#" id="delete_form" method="POST"> {{ method_field('DELETE') }} {{ csrf_field() }} <input type="submit" class="btn btn-danger pull-right delete-confirm" value="{{ __('voyager::generic.delete_confirm') }}"> </form> <button type="button" class="btn btn-default pull-right" data-dismiss="modal">{{ __('voyager::generic.cancel') }}</button> </div> </div><!-- /.modal-content --> </div><!-- /.m